  const filteredCoins = listOfCoins.filter((coin) => {
    return coin.name.toLowerCase().includes(searchWord.toLowerCase());
  });


  return (
    <div className="App">
      <div className='cryptoHeader'>
        <h1 className='head left'>Crypto Tracker</h1>
        <input type="text" className = "input right" placeholder = "Search Here...." onChange={(event) => {
            setSearchWord(event.target.value);
          }}/>
      </div>
      <div className='cryptoDisplay'>{filteredCoins.map((coin) =>{
        return <Coin name ={coin.name} icon = {coin.icon} price = {coin.price} symbol ={coin.symbol}/>
      })}</div>
    </div>
  );
}

export default App;
