import './App.css';
import {useEffect, useState} from 'react'
import Axios from 'axios'
import Coin from './components/coin'

function App() {

  // like creating varibles only diff is after function over variable destroy nut it doesn't do that.
  const [listOfCoins, setListOfCoins] = useState([]);
  const [searchWord, setSearchWord] = useState("");

  // immediate loading after page open and assigning
  useEffect(() => {
    useEffect(() => {
      const fetchData = async () => {

        let url = "http://13.127.98.159/api/search/village_level_search";
        let params = {
          village_no: formData.village,
          district_no: formData.district,
          block_no: formData.block,
          sub_div_code: formData.sub_div_code,
        };

        if (formData.khata_no) {
          params.khata_no = formData.khata_no;
        } else if (formData.khesra_no) {
          params.khesra_no = formData.khesra_no;
        }

        let finalUrl = url + "?" + new URLSearchParams(params).toString();

        console.log(finalUrl, "final api");

        try {
          const response = await Axios.get(finalUrl);
          console.log(response.data, "response from api");
          if (response.data.json_data) {
            setKhataData(response.data.json_data);
          }
        } catch (error) {
          console.error("Error:", error);
        } finally {
          setLoading(false);
        }
      };

      fetchData().catch((error) => console.error(error));
    }, [formData]);
    Axios.get("https://api.coinstats.app/public/v1/coins?skip=0&limit=10").then(
      (response) => {
        setListOfCoins(response.data.coins);
        console.log(response.data);
      }
    );
  }, []);

  const filteredCoins = listOfCoins.filter((coin) => {
    return coin.name.toLowerCase().includes(searchWord.toLowerCase());
  });


  return (
    <div className="App">
      <div className='cryptoHeader'>
        <h1 className='head left'>Crypto Tracker</h1>
        <input type="text" className = "input right" placeholder = "Search Here...." onChange={(event) => {
            setSearchWord(event.target.value);
          }}/>
      </div>
      <div className='cryptoDisplay'>{filteredCoins.map((coin) =>{
        return <Coin name ={coin.name} icon = {coin.icon} price = {coin.price} symbol ={coin.symbol}/>
      })}</div>
    </div>
  );
}

export default App;
