import './App.css';
import {useEffect, useState} from 'react'
import Axios from 'axios'
import Coin from './components/coin'

function App() {

  const [listOfCoins, setListOfCoins] = useState([]);
  useEffect(() => {
    Axios.get("https://api.coinstats.app/public/v1/coins?skip=0&limit=10").then(
      (response) => {
        setListOfCoins(response.data.coins);
        console.log(response.data);
      }
    );
  }, []);
  return (
    <div className="App">
      <div className='cryptoHeader'></div>
      <div className='cryptoDisplay'>{listOfCoins.map((coins) =>{
        return <Coin name ={coins.name} icon = {coin.icon} price = {coin.price} symbol ={coin.symbol}/>
      })}</div>
    </div>
  );
}

export default App;
